body {
    font-family: "Noto Sans", sans-serif;
    font-size: 16px;
    line-height: 24px;
    color: rgb(50, 50, 50);
    margin: 0;
    background-color: #383838;
    color: #d4d4d0;
}

body > ul {
    margin-bottom: 75px;
    padding: 0;
    list-style: none;
}

body > ul > li {
    margin-bottom: 50px;
    padding: 20px;
    border: 1px solid #565656;
}

h3, body > h2 a  {
    color: #f6b621!important;
}

h3:before {
	content: "";
	background: #f6b621;
}

a {
    color: #f6b621;
    text-decoration: none;
}

a:hover {
    text-decoration: underline;
}

blockquote {
    padding: 15px;
    background: #424242;
    border: 1px solid #565656;
}

ol {
    margin-bottom: 20px;
}

@media (max-width: 767px) {
    body {
		padding: 0 20px;
	}
    
    h3:before {
		display: block;
		height: 5px;
		margin-left: -20px;
		margin-right: -20px;
		margin-bottom: 10px;
	}
}

@media (min-width: 768px) {
    body > * {
    	display: block;
	    width: 728px;
	    margin-left: auto;
    	margin-right: auto;
	}
    
    h3:before {
	    display: inline-block;
		width: 1000px;
		height: 3px;
		margin-left: -1010px;
		margin-right: 10px;
	}
}

@media (min-width: 970px) {
    body > * {
		width: 900px;
	}
}
